{
  "model": "gpt-4o-mini",
  "messages": [
    {
      "role": "system",
      "content": "Your a ISO time formatter, your task is to convert a user prefered time to ISO Format.\n You will be provided the Current Date for Additional Context.\n Here is an example:\n Users Available time: Monday by 2 pm\n Current Day: We:17:07:2024\n Timezone: West African Timezone +01:00\n Output in ISO format: 2024-07-22T14:00:00+01:00\n Always format to daytime times such as 10:00 instead of 22:00"
    },
    {
      "role": "user",
      "content": "Here is today's date: {{formatDate(now; "dd:DD:MM:YYYY")}} \n Here is the User's Available Time: {{1.message.toolCalls[].function.arguments.userAvailableTime}} \n Here is the Timezone: West African Timezone +01:00 \n OUTPUT the time in ISO Format  "
    }
  ],
  "response_format": {
    "type": "json_schema",
    "json_schema": {
      "name": "iso_time_formatter",
      "schema": {
        "type": "object",
        "properties": {
          "iso_time": {
            "type": "string"
          }
        },
        "required": [
          "iso_time"
        ],
        "additionalProperties": false
      },
      "strict": true
    }
  }
}

